type TestFormula = P.Formula String AF
type TestTerm = P.Term AF
cv :: P.V -> C.Variable
cv (P.V s) = s
cp :: String -> C.Predicate
cp = id
cf :: AF -> AtomicFunction
cf (OtherFunction s) = AtomicFunction s
cf (SkolemFunction n) = AtomicSkolemFunction n
cv' :: C.Variable -> P.V
cv' = P.V
cp' :: C.Predicate -> C.Predicate
cp' = id
cf' :: AtomicFunction -> AF
cf' (AtomicFunction s) = OtherFunction s
cf' (AtomicSkolemFunction n) = SkolemFunction n


