pairTest :: (String, PropForm TestFormula, TestFormula) -> [Test]
pairTest (s, f1, f2) =
    [ TestCase (assertEqual (s ++ ", Chiou cnf") f1 (cnf' f2))
    , TestCase (assertEqual (s ++ ", PredicateLogic cnf")
                f1 (toPropositional A (runIdentity (evalStateT (cnf f2) 1)))) ]

-- |Here is an example of automatic conversion from a PredicateLogic
-- instance to a PropositionalLogic instance.  The result is PropForm
-- a where a is the original type, but the a values will always be
-- "atomic" formulas, never the operators which can be converted into
-- the corresponding operator of a PropositionalLogic instance.
test9a :: Test
test9a = TestCase 
           (assertEqual "convert to PropLogic"
            expected
            (flatten (cnf' (for_all ["x"] (for_all ["y"] (q [x, y] .<=>. for_all ["z"] (f [z, x] .<=>. f [z, y])))))))
    where
      f = pApp "f"
      q = pApp "q"
      convertA = A
      expected :: PropForm TestFormula
      expected = CJ [DJ [N (A (pApp ("q") [var ("x"),var ("y")])),
                         N (A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("x")])),
                         A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("y")])],
                     DJ [N (A (pApp ("q") [var ("x"),var ("y")])),
                         N (A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("y")])),
                         A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("x")])],
                     DJ [CJ [DJ [A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("x")]),
                                 A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("y")])],
                             DJ [A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("x")]),
                                 N (A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("x")]))],
                             DJ [N (A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("y")])),
                                 A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("y")])],
                             DJ [N (A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("y")])),
                                 N (A (pApp ("f") [fApp (SkolemFunction 1) [var ("x"),var ("y"),var ("z")],var ("x")]))]],
                         A (pApp ("q") [var ("x"),var ("y")])]]

moveQuantifiersOut1 :: Test
moveQuantifiersOut1 =
    formCase "moveQuantifiersOut1"
             (for_all ["y"] (pApp (fromString "p") [y] .&. (pApp (fromString "q") [x])))
             (prenexNormalForm (for_all ["x"] (pApp (fromString "p") [x]) .&. (pApp (fromString "q") [x])))

skolemize1 :: Test
skolemize1 =
    formCase "skolemize1" expected formula
    where
      expected :: TestFormula
      expected = for_all ["y","z"] (for_all ["v"] (pApp "P" [fApp (toEnum 1) [], y, z, fApp ((toEnum 2)) [y, z], v, fApp (toEnum 3) [y, z, v]]))
      formula :: TestFormula
      formula = (snf' (exists ["x"] (for_all ["y", "z"] (exists ["u"] (for_all ["v"] (exists ["w"] (pApp "P" [x, y, z, u, v, w])))))))

skolemize2 :: Test
skolemize2 =
    formCase "skolemize2" expected formula
    where
      expected :: TestFormula
      expected = for_all [fromString "y"] (pApp ("loves") [fApp (toEnum 1) [],y])
      formula :: TestFormula
      formula = snf' (exists ["x"] (for_all ["y"] (pApp "loves" [x, y])))

skolemize3 :: Test
skolemize3 =
    formCase "skolemize3" expected formula
    where
      expected :: TestFormula
      expected = for_all [fromString "y"] (pApp ("loves") [fApp (toEnum 1) [y],y])
      formula :: TestFormula
      formula = snf' (for_all ["y"] (exists ["x"] (pApp "loves" [x, y])))

